import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, Alert } from 'react-native';
import { loadActivities, saveActivities } from '../utils/storage';
import { useNavigation, useRoute } from '@react-navigation/native';

function dateKey(d = new Date()) {
  return new Date(d.getFullYear(), d.getMonth(), d.getDate()).toISOString().slice(0,10);
}

export default function LogActivityScreen() {
  const navigation = useNavigation();
  const route = useRoute();
  const prefill = route.params?.prefill ?? 'water';

  const [type, setType] = useState(prefill);
  const [value, setValue] = useState('');
  const [time, setTime] = useState(new Date().toISOString());
  const [notes, setNotes] = useState('');

  async function onSubmit() {
    // Validation
    if(!type) return Alert.alert('Validation', 'Please select activity type.');
    if(value === '' || isNaN(Number(value)) || Number(value) < 0) {
      return Alert.alert('Validation', 'Please enter a valid numeric value.');
    }

    const activities = await loadActivities();
    const newActivity = {
      id: Date.now().toString(),
      type,
      value: Number(value),
      time: new Date(time).toISOString(),
      notes,
      dateStr: dateKey(new Date(time))
    };
    const updated = [newActivity, ...activities];
    await saveActivities(updated);
    Alert.alert('Saved', 'Activity logged successfully.', [{ text: 'OK', onPress: () => navigation.navigate('Dashboard') }]);
  }

  return (
    <View style={{flex:1, padding:16}}>
      <Text style={{fontWeight:'700', fontSize:18, marginBottom:8}}>Log Activity</Text>

      <View style={{marginBottom:12}}>
        <Text style={{marginBottom:6}}>Activity Type</Text>
        <View style={{flexDirection:'row', gap:8}}>
          {['water','steps','sleep'].map(t => (
            <TouchableOpacity key={t} onPress={() => setType(t)} style={[styles.typeBtn, type===t && styles.typeBtnSelected]}>
              <Text style={type===t ? {color:'#fff'} : {}}>{t}</Text>
            </TouchableOpacity>
          ))}
        </View>
      </View>

      <View style={{marginBottom:12}}>
        <Text>Value</Text>
        <TextInput keyboardType="numeric" placeholder="e.g. 2 or 5000 or 7.5" value={String(value)} onChangeText={setValue} style={styles.input} />
      </View>

      <View style={{marginBottom:12}}>
        <Text>Time (auto-filled)</Text>
        <TextInput value={new Date(time).toLocaleString()} editable={false} style={[styles.input, {backgroundColor:'#fafafa'}]} />
      </View>

      <View style={{marginBottom:20}}>
        <Text>Notes (optional)</Text>
        <TextInput value={notes} onChangeText={setNotes} style={[styles.input, {height:80}]} multiline />
      </View>

      <TouchableOpacity style={styles.saveBtn} onPress={onSubmit}>
        <Text style={{color:'#fff', fontWeight:'700'}}>Save</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  typeBtn: { padding:10, borderRadius:8, backgroundColor:'#fff', marginRight:8 },
  typeBtnSelected: { backgroundColor:'#2f6bed' },
  input: { borderWidth:1, borderColor:'#eee', padding:10, borderRadius:8, marginTop:6 },
  saveBtn: { backgroundColor:'#2f6bed', padding:14, alignItems:'center', borderRadius:10 }
});
